mapper.select:
select count(*) 
from post

	where (post_title like concat('%', #{cri.search},'%') 
		or post_user_id = #{cri.search}) and post_board_num = #{board_id}


	where post_title like concat('%', #{cri.search},'%') and post_board_num = #{board_id}


	where post_user_id = #{cri.search} and post_board_num = #{board_id}

select * 
from post 

	where (post_title like concat('%', #{cri.search},'%') 
		or post_user_id = #{cri.search}) and post_board_num = #{board_id}


	where post_title like concat('%', #{cri.search},'%') and post_board_num = #{board_id}


	where post_user_id = #{cri.search} and post_board_num = #{board_id}

order by post_id desc
limit #{cri.pageStart}, #{cri.perPageNum}
